mapper.select:
SELECT c.ComIdx, c.ComCateIdx, c.MIdx, c.ComToCount, c.ComintoDate, c.ComTitle, c.ComContent, to_char(c.ComRegDate, 'YYYY-MM-DD HH24:MI') AS ComRegDate, to_char(c.ComDelDate, 'YYYY-MM-DD HH24:MI') AS ComDelDate, to_char(c.ComUpDate, 'YYYY-MM-DD HH24:MI') AS ComUpDate, to_char(c.ComStartDate, 'YYYY-MM-DD HH24:MI') AS ComStartDate, to_char(c.ComEndDate, 'YYYY-MM-DD HH24:MI') AS ComEndDate, COUNT(ts.MIdx) AS MemberCount FROM Tbl_Community c LEFT JOIN Tbl_TogetherStudy ts ON c.ComIdx = ts.ComIdx
WHERE c.ComCateIdx = #{categoryID}
GROUP BY c.ComIdx, c.ComCateIdx, c.MIdx, c.ComToCount, c.ComintoDate, c.ComTitle, c.ComContent, c.ComRegDate, c.ComDelDate, c.ComUpDate, c.ComStartDate, c.ComEndDate ORDER BY c.ComIdx
